import{_ as e,o as s,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-745 Special license plates for persons awarded the Medal of Honor; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-745.md","filePath":"vacode/46.2-745.md"}'),r={name:"vacode/46.2-745.md"},i=o('<h1 id="§-46-2-745-special-license-plates-for-persons-awarded-the-medal-of-honor-fees" tabindex="-1">§ 46.2-745 Special license plates for persons awarded the Medal of Honor; fees. <a class="header-anchor" href="#§-46-2-745-special-license-plates-for-persons-awarded-the-medal-of-honor-fees" aria-label="Permalink to &quot;§ 46.2-745 Special license plates for persons awarded the Medal of Honor; fees.&quot;">​</a></h1><p>On receipt of an application and written confirmation from one of the armed services that the applicant has been awarded the Medal of Honor, the Commissioner shall issue special license plates to such persons and to unremarried surviving spouses of such persons. No fee shall be charged for the issuance of these license plates.</p><p>It shall be unlawful for any person who is not a person described in this section to willfully and falsely represent himself as having the qualifications to obtain the special license plates herein provided for.</p><p>The provisions of subdivisions 1 and 2 of subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a> shall not apply to license plates issued under this section.</p><p>The design of license plates issued under this section to persons who have been awarded multiple decorations shall reflect the number of such decorations.</p><p>Unremarried surviving spouses of persons eligible to receive special license plates under this section may also be issued special license plates under this section.</p><p>1980, c. 55, § 46.1-105.8; 1989, c. 727; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',7),t=[i];function n(l,c,p,d,h,f){return s(),a("div",null,t)}const m=e(r,[["render",n]]);export{_ as __pageData,m as default};
