import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-749.130 Special license plates for supporters of the Surfrider Foundation; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-749.130.md","filePath":"vacode/46.2-749.130.md"}'),s={name:"vacode/46.2-749.130.md"},o=r('<h1 id="§-46-2-749-130-special-license-plates-for-supporters-of-the-surfrider-foundation-fees" tabindex="-1">§ 46.2-749.130 Special license plates for supporters of the Surfrider Foundation; fees. <a class="header-anchor" href="#§-46-2-749-130-special-license-plates-for-supporters-of-the-surfrider-foundation-fees" aria-label="Permalink to &quot;§ 46.2-749.130 Special license plates for supporters of the Surfrider Foundation; fees.&quot;">​</a></h1><p>A. On receipt of an application therefor and payment of the fee prescribed by this section, and following the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a>, other than those relating to the fee for the plates and its disposition, the Commissioner shall issue to the applicant special license plates for supporters of the Surfrider Foundation.</p><p>B. The annual fee for plates issued pursuant to this section shall be $25 in addition to the prescribed fee for state license plates. For each such $25 fee collected in excess of 1,000 registrations pursuant to this section, $15 shall be paid into the state treasury and credited to a special nonreverting fund known as the Surfrider Foundation Fund, established within the Department of Accounts. These funds shall be paid annually to the Surfrider Foundation and used by its Virginia Beach chapter to support the protection and enjoyment of oceans, waves, and beaches in Virginia. All other fees imposed under the provisions of this section shall be paid to, and received by, the Commissioner and paid by him into the state treasury and set aside as a special fund to be used to meet the necessary expenses incurred by the Department of Motor Vehicles.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0556" target="_blank" rel="noreferrer">556</a>.</p>',4),i=[o];function n(p,l,d,c,f,h){return t(),a("div",null,i)}const b=e(s,[["render",n]]);export{_ as __pageData,b as default};
