import{_ as e,o as t,c as i,O as s}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 46.2-749.7:3 Special license plates supporting education, charity, and scientific study for Virginia's Eastern Shore business community; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-749.7:3.md","filePath":"vacode/46.2-749.7:3.md"}`),a={name:"vacode/46.2-749.7:3.md"},n=s('<h1 id="§-46-2-749-7-3-special-license-plates-supporting-education-charity-and-scientific-study-for-virginia-s-eastern-shore-business-community-fees" tabindex="-1">§ 46.2-749.7:3 Special license plates supporting education, charity, and scientific study for Virginia&#39;s Eastern Shore business community; fees. <a class="header-anchor" href="#§-46-2-749-7-3-special-license-plates-supporting-education-charity-and-scientific-study-for-virginia-s-eastern-shore-business-community-fees" aria-label="Permalink to &quot;§ 46.2-749.7:3 Special license plates supporting education, charity, and scientific study for Virginia&#39;s Eastern Shore business community; fees.&quot;">​</a></h1><p>A. On receipt of an application therefor and payment of the fee prescribed by this section, and following the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-725/" target="_blank" rel="noreferrer">46.2-725</a>, other than those relating to the fee for the plates and its disposition, the Commissioner shall issue to the applicant special license plates promoting tourism on Virginia&#39;s Eastern Shore.</p><p>B. The annual fee for plates issued pursuant to this section shall be $25 in addition to the prescribed fee for state license plates. For each such $25 fee collected in excess of 1,000 registrations pursuant to this section, $15 shall be paid into the state treasury and credited to a special nonreverting fund known as the Eastern Shore Foundation Fund, established within the Department of Accounts. These funds shall be paid annually to the Eastern Shore of Virginia Chamber of Commerce Foundation and used to support education, charity, and scientific study for Virginia&#39;s Eastern Shore business community. All other fees imposed under the provisions of this section shall be paid to, and received by, the Commissioner of the Department of Motor Vehicles and paid by him into the state treasury and set aside as a special fund to be used to meet the necessary expenses incurred by the Department of Motor Vehicles.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0381" target="_blank" rel="noreferrer">381</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0662" target="_blank" rel="noreferrer">662</a>.</p>',4),o=[n];function r(c,l,p,d,h,u){return t(),i("div",null,o)}const _=e(a,[["render",r]]);export{m as __pageData,_ as default};
