import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-800.1 Riding animals on highways after sunset.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-800.1.md","filePath":"vacode/46.2-800.1.md"}'),s={name:"vacode/46.2-800.1.md"},o=t('<h1 id="§-46-2-800-1-riding-animals-on-highways-after-sunset" tabindex="-1">§ 46.2-800.1 Riding animals on highways after sunset. <a class="header-anchor" href="#§-46-2-800-1-riding-animals-on-highways-after-sunset" aria-label="Permalink to &quot;§ 46.2-800.1 Riding animals on highways after sunset.&quot;">​</a></h1><p>A. No person riding upon any animal on a highway between sunset and sunrise shall ride the animal on the roadway unless the rider:</p><ol><li><p>Wears a hat made of or coated with reflectorized material; or</p></li><li><p>Wears upper body clothing made of or coated with reflectorized material visible from 360°; or</p></li><li><p>Displays at least 100 square inches of solid reflectorized material at shoulder level visible from 360°; or</p></li><li><p>Carries a light visible in clear weather from a distance of 500 feet.</p></li></ol><p>B. The requirements of subsection A of this section shall only apply to the riders of the first and last animals in a group riding one behind the other.</p><p>C. A violation of this section shall not be construed as negligence per se in any civil action.</p><p>1989, c. 295, § 46.1-171.01.</p>',6),n=[o];function r(l,d,h,c,p,f){return a(),i("div",null,n)}const u=e(s,[["render",r]]);export{m as __pageData,u as default};
