import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-808.1 Use of crossovers on controlled access highways; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-808.1.md","filePath":"vacode/46.2-808.1.md"}'),t={name:"vacode/46.2-808.1.md"},i=o('<h1 id="§-46-2-808-1-use-of-crossovers-on-controlled-access-highways-penalty" tabindex="-1">§ 46.2-808.1 Use of crossovers on controlled access highways; penalty. <a class="header-anchor" href="#§-46-2-808-1-use-of-crossovers-on-controlled-access-highways-penalty" aria-label="Permalink to &quot;§ 46.2-808.1 Use of crossovers on controlled access highways; penalty.&quot;">​</a></h1><p>It is unlawful for the driver of any vehicle other than an authorized vehicle to use or attempt to use any crossover posted for authorized vehicles only on any controlled access highway.</p><p>For the purposes of this section, &quot;authorized vehicle&quot; means (i) Department of Transportation vehicles; (ii) law-enforcement vehicles; (iii) emergency vehicles as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-920/" target="_blank" rel="noreferrer">46.2-920</a>; (iv) towing and recovery vehicles operating under the direction of a law-enforcement agency or the Department of Transportation; (v) vehicles for which permits authorizing use of such crossovers have been issued by the Department of Transportation; (vi) vehicles operated pursuant to a Department of Transportation safety service patrol program as defined in subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-920.1/" target="_blank" rel="noreferrer">46.2-920.1</a> when engaged in providing services under such program; (vii) vehicles operated pursuant to a contract with the Department of Transportation for, or that includes, traffic incident management services as defined in subsection B of § <a href="http://law.lis.virginia.gov/vacode/46.2-920.1/" target="_blank" rel="noreferrer">46.2-920.1</a> when providing such traffic incident management services; and (viii) other vehicles operating in medical emergency situations.</p><p>Violation of any provision of this section shall constitute a traffic infraction punishable by a fine of no more than $250.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0470" target="_blank" rel="noreferrer">470</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0647" target="_blank" rel="noreferrer">647</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0350" target="_blank" rel="noreferrer">350</a>.</p>',5),n=[i];function s(c,l,h,f,p,v){return r(),a("div",null,n)}const u=e(t,[["render",s]]);export{g as __pageData,u as default};
