import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-808 Commonwealth Transportation Board may prohibit certain uses of controlled access highways; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-808.md","filePath":"vacode/46.2-808.md"}'),r={name:"vacode/46.2-808.md"},t=i('<h1 id="§-46-2-808-commonwealth-transportation-board-may-prohibit-certain-uses-of-controlled-access-highways-penalty" tabindex="-1">§ 46.2-808 Commonwealth Transportation Board may prohibit certain uses of controlled access highways; penalty. <a class="header-anchor" href="#§-46-2-808-commonwealth-transportation-board-may-prohibit-certain-uses-of-controlled-access-highways-penalty" aria-label="Permalink to &quot;§ 46.2-808 Commonwealth Transportation Board may prohibit certain uses of controlled access highways; penalty.&quot;">​</a></h1><p>A. The Commonwealth Transportation Board may, when necessary to promote safety, prohibit the use of controlled access highways or any part thereof by any or all of the following:</p><ol><li><p>Pedestrians,</p></li><li><p>Persons riding bicycles, electric power-assisted bicycles, electric personal assistive mobility devices, or mopeds,</p></li><li><p>Animal-drawn vehicles,</p></li><li><p>Self-propelled machinery or equipment, and</p></li><li><p>Animals led, ridden or driven on the hoof.</p></li></ol><p>B. The termini of any section of controlled access highways, use of which is restricted under the provisions of this section, shall be clearly indicated by a conspicuous marker.</p><p>C. This section shall not apply to any vehicle or equipment owned or controlled by the Virginia Department of Transportation, while actually engaged in the construction, reconstruction, or maintenance of highways or to any vehicle or equipment for which a permit has been obtained for operation on such highway.</p><p>Any person violating a restriction or prohibition imposed pursuant to this section shall be guilty of a traffic infraction.</p><p>1964, c. 239, § 46.1-171.1; 1966, c. 365; 1981, c. 585; 1983, c. 262; 1989, c. 727; 1991, c. 55; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0973" target="_blank" rel="noreferrer">973</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0209" target="_blank" rel="noreferrer">209</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0366" target="_blank" rel="noreferrer">366</a>.</p>',7),n=[t];function l(s,c,h,p,d,f){return a(),o("div",null,n)}const y=e(r,[["render",l]]);export{m as __pageData,y as default};
