import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-810.1 Smoking in vehicle with a minor present; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-810.1.md","filePath":"vacode/46.2-810.1.md"}'),r={name:"vacode/46.2-810.1.md"},a=o('<h1 id="§-46-2-810-1-smoking-in-vehicle-with-a-minor-present-civil-penalty" tabindex="-1">§ 46.2-810.1 Smoking in vehicle with a minor present; civil penalty. <a class="header-anchor" href="#§-46-2-810-1-smoking-in-vehicle-with-a-minor-present-civil-penalty" aria-label="Permalink to &quot;§ 46.2-810.1 Smoking in vehicle with a minor present; civil penalty.&quot;">​</a></h1><p>A. For the purposes of this section, &quot;smoke&quot; means to carry or hold any lighted pipe, cigar, or cigarette of any kind or any other lighted smoking equipment or to light or inhale or exhale smoke from a pipe, cigar, or cigarette of any kind or any other lighted smoking equipment.</p><p>B. It is unlawful for a person to smoke in a motor vehicle, whether in motion or at rest, when a minor under the age of 15 is present in the motor vehicle. A violation of this section is punishable by a civil penalty of $100 to be paid into the state treasury and credited to the Literary Fund. No demerit points shall be assigned under Article 19 (§ <a href="/vacode/46.2-489/">46.2-489</a> et seq.) of Chapter 3 and no court costs shall be assessed for a violation of this section. A violation of this section may be charged on the uniform traffic summons form.</p><p>C. No law-enforcement officer shall stop a motor vehicle for a violation of this section. No evidence discovered or obtained as the result of a stop in violation of this subsection, including evidence discovered or obtained with the operator&#39;s consent, shall be admissible in any trial, hearing, or other proceeding.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0515" target="_blank" rel="noreferrer">515</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0972" target="_blank" rel="noreferrer">972</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>.</p>',5),n=[a];function s(l,c,h,p,d,f){return i(),t("div",null,n)}const v=e(r,[["render",s]]);export{m as __pageData,v as default};
