import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 46.2-818 Stopping vehicle of another; blocking access to premises; damaging or threatening commercial vehicle or operator thereof; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-818.md","filePath":"vacode/46.2-818.md"}'),i={name:"vacode/46.2-818.md"},a=t('<h1 id="§-46-2-818-stopping-vehicle-of-another-blocking-access-to-premises-damaging-or-threatening-commercial-vehicle-or-operator-thereof-penalties" tabindex="-1">§ 46.2-818 Stopping vehicle of another; blocking access to premises; damaging or threatening commercial vehicle or operator thereof; penalties. <a class="header-anchor" href="#§-46-2-818-stopping-vehicle-of-another-blocking-access-to-premises-damaging-or-threatening-commercial-vehicle-or-operator-thereof-penalties" aria-label="Permalink to &quot;§ 46.2-818 Stopping vehicle of another; blocking access to premises; damaging or threatening commercial vehicle or operator thereof; penalties.&quot;">​</a></h1><p>No person shall intentionally and willfully:</p><ol><li><p>Stop the vehicle of another for the sole purpose of impeding its progress on the highways, except in the case of an emergency or mechanical breakdown;</p></li><li><p>Block the access to or egress from any premises of any service facility operated for the purposes of (i) selling fuel for motor vehicles, (ii) performing repair services on motor vehicles, or (iii) furnishing food, rest, or any other convenience for the use of persons operating motor vehicles engaged in intrastate and interstate commerce on the highways of the Commonwealth;</p></li><li><p>Damage any vehicle engaged in commerce on the highways of the Commonwealth, or threaten, assault, or otherwise harm the person of any operator of a motor vehicle being used for the transportation of property for hire.</p></li></ol><p>Any person violating any provision of this section is guilty of a Class 1 misdemeanor, and in addition, his driver&#39;s license may be suspended by the court for a period of not more than one year. The court shall forward such license to the Department as provided by § <a href="http://law.lis.virginia.gov/vacode/46.2-398/" target="_blank" rel="noreferrer">46.2-398</a>.</p><p>The provisions of this section shall not apply to any law-enforcement officer, school guard, firefighter, or emergency medical services personnel engaged in the performance of his duties nor to any vehicle owned or controlled by the Virginia Department of Transportation while engaged in the construction, reconstruction, or maintenance of highways.</p><p>1974, c. 457, § 46.1-250.1; 1977, c. 326; 1984, c. 780; 1989, c. 727; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>.</p>',6),n=[a];function s(c,l,h,p,g,f){return o(),r("div",null,n)}const v=e(i,[["render",s]]);export{d as __pageData,v as default};
