import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-819.10 Withholding of vehicle registration for enforcement of out-of-state toll violations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-819.10.md","filePath":"vacode/46.2-819.10.md"}'),a={name:"vacode/46.2-819.10.md"},r=i('<h1 id="§-46-2-819-10-withholding-of-vehicle-registration-for-enforcement-of-out-of-state-toll-violations" tabindex="-1">§ 46.2-819.10 Withholding of vehicle registration for enforcement of out-of-state toll violations. <a class="header-anchor" href="#§-46-2-819-10-withholding-of-vehicle-registration-for-enforcement-of-out-of-state-toll-violations" aria-label="Permalink to &quot;§ 46.2-819.10 Withholding of vehicle registration for enforcement of out-of-state toll violations.&quot;">​</a></h1><p>A. Upon receipt of notice from a state that has entered into an agreement with the Commonwealth pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-819.9/" target="_blank" rel="noreferrer">46.2-819.9</a> that a resident of Virginia owes unpaid tolls, administrative fees, or penalties to that state, the Commissioner shall refuse to issue or renew the vehicle registration certificate or the license plate issued for a vehicle or vehicles owned by such resident in accordance with this section until such state has notified the Commissioner that such tolls, fees, or penalties have been paid.</p><p>If the resident is the owner and operator of the vehicle used in the commission of the offense, the Commissioner shall refuse to issue or renew the vehicle registration certificate or the license plate issued for that vehicle. If the resident was the operator of the vehicle, but not the owner, the Commissioner shall refuse to issue or renew any vehicle registration certificate or license plates for any vehicle owned by the resident.</p><p>B. The Department shall send each resident identified pursuant to subsection A notice of the intent to deny renewal of registration at least 30 days prior to the expiration date of a current vehicle registration. Such notice shall include instructions for contacting the state to which the unpaid tolls, administrative fees, or penalties are owed by the resident and indicate that such contact information is provided for the purpose of payment of the amounts owed.</p><p>C. Upon receipt of notice from the applicable state that the resident has satisfied all outstanding obligations to that state, the Commissioner shall release the hold on the vehicle registrations and permit the same to be issued or renewed.</p><p>D. The Commissioner shall charge a $40 fee to defray the cost of processing and withholding the registration or registration renewal under this section.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0753" target="_blank" rel="noreferrer">753</a>.</p>',7),n=[r];function s(h,l,c,f,d,p){return t(),o("div",null,n)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
