import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-831 Unofficial traffic control devices prohibited; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-831.md","filePath":"vacode/46.2-831.md"}'),o={name:"vacode/46.2-831.md"},r=a('<h1 id="§-46-2-831-unofficial-traffic-control-devices-prohibited-penalties" tabindex="-1">§ 46.2-831 Unofficial traffic control devices prohibited; penalties. <a class="header-anchor" href="#§-46-2-831-unofficial-traffic-control-devices-prohibited-penalties" aria-label="Permalink to &quot;§ 46.2-831 Unofficial traffic control devices prohibited; penalties.&quot;">​</a></h1><p>No unauthorized person shall erect or maintain on any highway any warning or direction sign, signal, or light in imitation of any official traffic control device erected as provided by law. No person shall erect or maintain on any highway any traffic control device bearing any commercial advertising.</p><p>Nothing in this section shall prohibit the erection or maintenance of signs or signals bearing the name of an organization authorized to erect it by the Commonwealth Transportation Board, the Department of Transportation, or local authorities of counties, cities, and towns as provided by law. Nor shall this section be construed to prohibit the erection by contractors or public utility companies of temporary signs approved by the Virginia Department of Transportation warning motorists that work is in progress on or adjacent to the highway.</p><p>Any violation of this section shall constitute a Class 4 misdemeanor.</p><p>Code 1950, § 46-187; 1958, c. 541, § 46.1-174; 1982, c. 681; 1989, c. 727; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0128" target="_blank" rel="noreferrer">128</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0400" target="_blank" rel="noreferrer">400</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>.</p>',5),n=[r];function c(s,l,f,h,p,d){return i(),t("div",null,n)}const b=e(o,[["render",c]]);export{_ as __pageData,b as default};
