import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 46.2-838 Passing when overtaking a vehicle.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-838.md","filePath":"vacode/46.2-838.md"}'),r={name:"vacode/46.2-838.md"},n=t('<h1 id="§-46-2-838-passing-when-overtaking-a-vehicle" tabindex="-1">§ 46.2-838 Passing when overtaking a vehicle. <a class="header-anchor" href="#§-46-2-838-passing-when-overtaking-a-vehicle" aria-label="Permalink to &quot;§ 46.2-838 Passing when overtaking a vehicle.&quot;">​</a></h1><p>A. The driver of any vehicle overtaking another vehicle proceeding in the same direction shall pass at least two feet to the left of the overtaken vehicle and shall not again drive to the right side of the highway until safely clear of such overtaken vehicle, except as otherwise provided in this article.</p><p>B. The driver of any motor vehicle, upon overtaking a stationary vehicle that is displaying a flashing, blinking, or alternating amber light as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-892/" target="_blank" rel="noreferrer">46.2-892</a> or subdivision A 10 of § <a href="http://law.lis.virginia.gov/vacode/46.2-1025/" target="_blank" rel="noreferrer">46.2-1025</a>, shall proceed with due caution and maintain a safe speed for highway conditions.</p><p>C. The driver of any motor vehicle, upon overtaking a stationary vehicle in the process of refuse collection operations, shall (i) on a highway having at least four lanes, at least two of which are intended for traffic proceeding in the same direction as the approaching vehicle, proceed with caution and, if reasonable, with due regard for safety and traffic conditions, yield the right-of-way by making a lane change into a lane not adjacent to the stationary vehicle or (ii) if changing lanes would be unreasonable or unsafe or on highways having fewer than four lanes, proceed with due caution and decrease speed to 10 miles per hour below the posted speed limit and pass at least two feet to the left of the vehicle.</p><p>Code 1950, § 46-224; 1958, c. 541, § 46.1-208; 1989, c. 727; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0999" target="_blank" rel="noreferrer">999</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0973" target="_blank" rel="noreferrer">973</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0189" target="_blank" rel="noreferrer">189</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0197" target="_blank" rel="noreferrer">197</a>.</p>',5),o=[n];function l(h,s,c,g,d,f){return a(),i("div",null,o)}const _=e(r,[["render",l]]);export{p as __pageData,_ as default};
