import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-841 When overtaking vehicle may pass on right.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-841.md","filePath":"vacode/46.2-841.md"}'),i={name:"vacode/46.2-841.md"},n=t('<h1 id="§-46-2-841-when-overtaking-vehicle-may-pass-on-right" tabindex="-1">§ 46.2-841 When overtaking vehicle may pass on right. <a class="header-anchor" href="#§-46-2-841-when-overtaking-vehicle-may-pass-on-right" aria-label="Permalink to &quot;§ 46.2-841 When overtaking vehicle may pass on right.&quot;">​</a></h1><p>A. The driver of a vehicle may overtake and pass to the right of another vehicle only:</p><ol><li><p>When the overtaken vehicle is making or about to make a left turn, and its driver has given the required signal;</p></li><li><p>On a highway with unobstructed pavement, not occupied by parked vehicles, of sufficient width for two or more lines of moving vehicles in each direction;</p></li><li><p>On a one-way street or on any one-way roadway when the roadway is free from obstructions and of sufficient width for two or more lines of moving vehicles.</p></li></ol><p>B. The driver of a vehicle may overtake and pass another vehicle on the right only under conditions permitting such movement in safety. Except where driving on paved shoulders is permitted by lawfully placed signs, no such movement shall be made by driving on the shoulder of the highway or off the pavement or main traveled portion of the roadway.</p><p>C. Notwithstanding subsections A and B, nothing in this section shall permit a driver of a motor vehicle to cross a solid line designating a bicycle lane to pass or attempt to pass another vehicle, except as provided in § <a href="/vacode/46.2-920.1/">46.2-920.1</a>, <a href="/vacode/46.2-1210/">46.2-1210</a>, or <a href="/vacode/46.2-1212.1/">46.2-1212.1</a>, as directed by a law-enforcement officer, or where the roadway is otherwise impassable due to weather conditions, an accident, or an emergency situation.</p><p>Code 1950, § 46-226; 1952, c. 666; 1958, c. 541, § 46.1-210; 1985, c. 481; 1989, c. 727; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1259" target="_blank" rel="noreferrer">1259</a>.</p>',6),r=[n];function s(h,c,d,l,p,v){return a(),o("div",null,r)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
