import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-867 Racing; seizure of motor vehicle.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-867.md","filePath":"vacode/46.2-867.md"}'),o={name:"vacode/46.2-867.md"},t=r('<h1 id="§-46-2-867-racing-seizure-of-motor-vehicle" tabindex="-1">§ 46.2-867 Racing; seizure of motor vehicle. <a class="header-anchor" href="#§-46-2-867-racing-seizure-of-motor-vehicle" aria-label="Permalink to &quot;§ 46.2-867 Racing; seizure of motor vehicle.&quot;">​</a></h1><p>If the owner of a motor vehicle (i) is convicted of racing such vehicle in a prearranged, organized, and planned speed competition in violation of § <a href="http://law.lis.virginia.gov/vacode/46.2-865/" target="_blank" rel="noreferrer">46.2-865</a>, (ii) is present in the vehicle which is being operated by another in violation of § <a href="http://law.lis.virginia.gov/vacode/46.2-865/" target="_blank" rel="noreferrer">46.2-865</a>, and knowingly consents to the racing, or (iii) is convicted of a violation of § <a href="http://law.lis.virginia.gov/vacode/46.2-865.1/" target="_blank" rel="noreferrer">46.2-865.1</a>, the vehicle shall be seized and shall be forfeited to the Commonwealth, and upon being condemned as forfeited in proceedings under Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2, the proceeds of sale shall be disposed of according to law. Such sections shall apply mutatis mutandis.</p><p>The penalties imposed by these sections are in addition to any other penalty imposed by law.</p><p>1972, c. 702, § 46.1-191.2; 1989, c. 727; 1993, c. 866; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',4),n=[t];function l(c,s,h,d,f,p){return a(),i("div",null,n)}const _=e(o,[["render",l]]);export{v as __pageData,_ as default};
