import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-874.1 Exceptions to maximum speed limits in residence districts; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-874.1.md","filePath":"vacode/46.2-874.1.md"}'),o={name:"vacode/46.2-874.1.md"},n=a('<h1 id="§-46-2-874-1-exceptions-to-maximum-speed-limits-in-residence-districts-penalty" tabindex="-1">§ 46.2-874.1 Exceptions to maximum speed limits in residence districts; penalty. <a class="header-anchor" href="#§-46-2-874-1-exceptions-to-maximum-speed-limits-in-residence-districts-penalty" aria-label="Permalink to &quot;§ 46.2-874.1 Exceptions to maximum speed limits in residence districts; penalty.&quot;">​</a></h1><p>A. The governing body of any town with a population between 14,000 and 15,000 may by ordinance (i) prohibit the operation of a motor vehicle at a speed of twenty miles per hour or more in excess of the applicable maximum speed limit in a residence district and (ii) provide that any person who violates the prohibition shall be subject to a mandatory civil penalty of $100, not subject to suspension.</p><p>B. The governing body of the City of Falls Church, or the City of Manassas may by ordinance (i) prohibit the operation of a motor vehicle at a speed of fifteen miles per hour or more in excess of the applicable maximum speed limit in a residence district, as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a> of the Code of Virginia, when indicated by appropriately placed signs displaying the maximum speed limit and the penalty for violations, and (ii) provide that any person who violates the prohibition shall be subject to a civil penalty of $100, in addition to other penalty provided by law.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0865" target="_blank" rel="noreferrer">865</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0957" target="_blank" rel="noreferrer">957</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>.</p>',4),r=[n];function s(l,p,c,d,h,m){return i(),t("div",null,r)}const b=e(o,[["render",s]]);export{_ as __pageData,b as default};
