import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-878.1 Maximum speed limits in highway work zones; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-878.1.md","filePath":"vacode/46.2-878.1.md"}'),t={name:"vacode/46.2-878.1.md"},r=o('<h1 id="§-46-2-878-1-maximum-speed-limits-in-highway-work-zones-penalty" tabindex="-1">§ 46.2-878.1 Maximum speed limits in highway work zones; penalty. <a class="header-anchor" href="#§-46-2-878-1-maximum-speed-limits-in-highway-work-zones-penalty" aria-label="Permalink to &quot;§ 46.2-878.1 Maximum speed limits in highway work zones; penalty.&quot;">​</a></h1><p>Operation of any motor vehicle in excess of a maximum speed limit established specifically for a highway work zone, when workers are present and when such highway work zone is indicated by appropriately placed signs displaying the maximum speed limit and the penalty for violations, shall be unlawful and constitute a traffic infraction punishable by a fine of not more than $500.</p><p>For the purposes of this section, &quot;highway work zone&quot; means a construction or maintenance area that is located on or beside a highway and marked by appropriate warning signs and, for projects covered by contracts entered into on or after July 1, 2012, with attached flashing lights or other traffic control devices indicating that work is in progress.</p><p>Nothing in this section shall preclude the prosecution or conviction for reckless driving of any motor vehicle operator whose operation of any motor vehicle in a highway work zone, apart from speed, demonstrates a reckless disregard for life, limb, or property.</p><p>1992, c. 462; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0054" target="_blank" rel="noreferrer">54</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>.</p>',5),n=[r];function s(l,c,h,p,d,m){return a(),i("div",null,n)}const _=e(t,[["render",s]]);export{g as __pageData,_ as default};
