import{_ as t,o as d,c as e,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-880 Tables of speed and stopping distances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-880.md","filePath":"vacode/46.2-880.md"}'),a={name:"vacode/46.2-880.md"},o=r('<h1 id="§-46-2-880-tables-of-speed-and-stopping-distances" tabindex="-1">§ 46.2-880 Tables of speed and stopping distances. <a class="header-anchor" href="#§-46-2-880-tables-of-speed-and-stopping-distances" aria-label="Permalink to &quot;§ 46.2-880 Tables of speed and stopping distances.&quot;">​</a></h1><p>All courts shall take notice of the following tables of speed and stopping distances of motor vehicles, which shall not raise a presumption, in actions in which inquiry thereon is pertinent to the issues:</p><table><thead><tr><th></th><th></th><th></th><th></th></tr></thead><tbody><tr><td>a</td><td>SPEED IN</td><td>AVERAGE STOPPING DISTANCES</td><td>TOTAL STOPPING</td></tr><tr><td>b</td><td></td><td></td><td></td></tr><tr><td>c</td><td></td><td></td><td></td></tr><tr><td>d</td><td>Miles</td><td>Feet</td><td>Automobile</td></tr><tr><td>e</td><td>Per</td><td>Per</td><td>Brakes</td></tr><tr><td>f</td><td>Hour</td><td>Second</td><td>(In Feet)</td></tr><tr><td>g</td><td>10</td><td>14.7</td><td>5</td></tr><tr><td>h</td><td>15</td><td>22.0</td><td>11</td></tr><tr><td>i</td><td>20</td><td>29.3</td><td>19</td></tr><tr><td>j</td><td>25</td><td>36.7</td><td>30</td></tr><tr><td>k</td><td>30</td><td>44.0</td><td>43</td></tr><tr><td>l</td><td>35</td><td>51.3</td><td>58</td></tr><tr><td>m</td><td>40</td><td>58.7</td><td>76</td></tr><tr><td>n</td><td>45</td><td>66.0</td><td>96</td></tr><tr><td>o</td><td>50</td><td>73.3</td><td>119</td></tr><tr><td>p</td><td>55</td><td>80.7</td><td>144</td></tr><tr><td>q</td><td>60</td><td>88.0</td><td>171</td></tr><tr><td>r</td><td>65</td><td>95.3</td><td>201</td></tr><tr><td>s</td><td>70</td><td>102.7</td><td>233</td></tr><tr><td>t</td><td>75</td><td>110.0</td><td>268</td></tr><tr><td>u</td><td>80</td><td>117.3</td><td>305</td></tr><tr><td>v</td><td>85</td><td>124.7</td><td>344</td></tr><tr><td>w</td><td>90</td><td>132.0</td><td>386</td></tr><tr><td>x</td><td>95</td><td>139.3</td><td>430</td></tr><tr><td>y</td><td>100</td><td>146.7</td><td>476</td></tr></tbody></table><p>The courts shall further take notice that the above table has been constructed, using scientific reasoning, to provide factfinders with an average baseline for motor vehicle stopping distances: (1) for a vehicle in good condition and (2) on a level, dry stretch of highway, free from loose material.</p><p>Deviations from these circumstances do not negate the usefulness of the table, but rather call for additional site-specific examination and/or explanation.</p><p>Site-specific research may be utilized under any circumstances.</p><p>Code 1950, § 46-212.2; 1956, c. 600; 1958, c. 541, § 46.1-195; 1989, c. 727; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0277" target="_blank" rel="noreferrer">277</a>.</p>',7),s=[o];function i(n,c,l,h,p,f){return d(),e("div",null,s)}const g=t(a,[["render",i]]);export{u as __pageData,g as default};
