import{_ as i,o as a,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-881 Special speed limitation on bridges, tunnels and interstates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-881.md","filePath":"vacode/46.2-881.md"}'),n={name:"vacode/46.2-881.md"},o=e("h1",{id:"§-46-2-881-special-speed-limitation-on-bridges-tunnels-and-interstates",tabindex:"-1"},[t("§ 46.2-881 Special speed limitation on bridges, tunnels and interstates. "),e("a",{class:"header-anchor",href:"#§-46-2-881-special-speed-limitation-on-bridges-tunnels-and-interstates","aria-label":'Permalink to "§ 46.2-881 Special speed limitation on bridges, tunnels and interstates."'},"​")],-1),r=e("p",null,"It shall be unlawful to drive any motor vehicle, trailer, or semitrailer on any public bridge, causeway, viaduct, or in any tunnel, or on any interstate at a speed exceeding that indicated as a maximum by signs posted thereon or at its approach by or on the authority of the Commissioner of Highways.",-1),d=e("p",null,"The Commissioner of Highways, on request or on his own initiative, may conduct an investigation of any public bridge, causeway, viaduct, tunnel, or interstate and, on the basis of his findings, may set the maximum speed of vehicles which such structure or roadway can withstand or which is necessitated in consideration of the benefit and safety of the traveling public and the safety of the structure or roadway. The Commissioner of Highways is expressly authorized to establish and indicate variable speed limits on such structures or roadways to be effective under such conditions as would in his judgment, warrant such variable limits, including but not limited to darkness, traffic conditions, atmospheric conditions, weather, emergencies, and like conditions which may affect driving safety. Any speed limits, whether fixed or variable, shall be prominently posted in such proximity to such structure or roadway as deemed appropriate by the Commissioner of Highways. The findings of the Commissioner shall be conclusive evidence of the maximum safe speed which can be maintained on such structure or roadway.",-1),c=e("p",null,[t("Code 1950, § 46-215; 1958, c. 541, § 46.1-196; 1966, c. 85; 1977, c. 259; 1989, c. 727; 2006, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0139",target:"_blank",rel:"noreferrer"},"139"),t(".")],-1),l=[o,r,d,c];function h(u,m,p,f,y,b){return a(),s("div",null,l)}const v=i(n,[["render",h]]);export{_ as __pageData,v as default};
