import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-900 Penalty for violation of §§ 46.2-895 through 46.2-897.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-900.md","filePath":"vacode/46.2-900.md"}'),r={name:"vacode/46.2-900.md"},i=a('<h1 id="§-46-2-900-penalty-for-violation-of-§§-46-2-895-through-46-2-897" tabindex="-1">§ 46.2-900 Penalty for violation of §§ 46.2-895 through 46.2-897. <a class="header-anchor" href="#§-46-2-900-penalty-for-violation-of-§§-46-2-895-through-46-2-897" aria-label="Permalink to &quot;§ 46.2-900 Penalty for violation of §§ 46.2-895 through 46.2-897.&quot;">​</a></h1><p>Any person convicted of violating the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/46.2-895/" target="_blank" rel="noreferrer">46.2-895</a> through <a href="http://law.lis.virginia.gov/vacode/46.2-897/" target="_blank" rel="noreferrer">46.2-897</a> shall, if such accident results in injury to or the death of any person, be guilty of a Class 6 felony. If such accident results only in damage to property, the person so convicted shall be guilty of a Class 1 misdemeanor; however, if the vehicle or other property struck is unattended and such damage is less than $250, such person shall be guilty of a Class 4 misdemeanor. A motor vehicle operator convicted of a Class 4 misdemeanor under this section shall be assigned three demerit points by the Commissioner of the Department of Motor Vehicles.</p><p>Code 1950, § 46-190; 1958, c. 541, § 46.1-177; 1962, c. 302; 1973, c. 8; 1979, c. 653; 1989, c. 727; 1992, c. 279; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0808" target="_blank" rel="noreferrer">808</a>.</p>',3),n=[i];function s(l,c,h,d,f,p){return o(),t("div",null,n)}const v=e(r,[["render",s]]);export{u as __pageData,v as default};
