import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-902.1 Officer may require certain motorists to furnish proof of insurance or payment of fee for registration of an uninsured motor vehicle; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-902.1.md","filePath":"vacode/46.2-902.1.md"}'),i={name:"vacode/46.2-902.1.md"},a=t('<h1 id="§-46-2-902-1-officer-may-require-certain-motorists-to-furnish-proof-of-insurance-or-payment-of-fee-for-registration-of-an-uninsured-motor-vehicle-penalty" tabindex="-1">§ 46.2-902.1 Officer may require certain motorists to furnish proof of insurance or payment of fee for registration of an uninsured motor vehicle; penalty. <a class="header-anchor" href="#§-46-2-902-1-officer-may-require-certain-motorists-to-furnish-proof-of-insurance-or-payment-of-fee-for-registration-of-an-uninsured-motor-vehicle-penalty" aria-label="Permalink to &quot;§ 46.2-902.1 Officer may require certain motorists to furnish proof of insurance or payment of fee for registration of an uninsured motor vehicle; penalty.&quot;">​</a></h1><p>Any law-enforcement officer present at the scene of a motor vehicle accident as to which a law-enforcement officer is required by § <a href="http://law.lis.virginia.gov/vacode/46.2-373/" target="_blank" rel="noreferrer">46.2-373</a> to file an accident report with the Department may require the operator of any motor vehicle involved in such accident to furnish proof that the vehicle he was operating at the time of such accident was either (i) an insured motor vehicle as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-705/" target="_blank" rel="noreferrer">46.2-705</a> or (ii) a vehicle for which the fee required by § <a href="http://law.lis.virginia.gov/vacode/46.2-706/" target="_blank" rel="noreferrer">46.2-706</a> for registration of an uninsured vehicle had been paid as to that vehicle. Failure to furnish proof of insurance or payment of the uninsured vehicle registration fee when required by a law-enforcement officer as provided in this section within thirty days shall constitute a Class 2 misdemeanor.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0450" target="_blank" rel="noreferrer">450</a>.</p>',3),n=[a];function f(s,c,h,l,u,d){return r(),o("div",null,n)}const _=e(i,[["render",f]]);export{m as __pageData,_ as default};
