import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-907 Overtaking and passing vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-907.md","filePath":"vacode/46.2-907.md"}'),t={name:"vacode/46.2-907.md"},o=r('<h1 id="§-46-2-907-overtaking-and-passing-vehicles" tabindex="-1">§ 46.2-907 Overtaking and passing vehicles. <a class="header-anchor" href="#§-46-2-907-overtaking-and-passing-vehicles" aria-label="Permalink to &quot;§ 46.2-907 Overtaking and passing vehicles.&quot;">​</a></h1><p>A person riding a bicycle, electric personal assistive mobility device, electric power-assisted bicycle, motorized skateboard or foot-scooter, or moped may overtake and pass another vehicle on either the left or right side, staying in the same lane as the overtaken vehicle, or changing to a different lane, or riding off the roadway as necessary to pass with safety.</p><p>A person riding a bicycle, electric personal assistive mobility device, electric power-assisted bicycle, motorized skateboard or foot-scooter, or moped may overtake and pass another vehicle only under conditions that permit the movement to be made with safety.</p><p>A person riding a bicycle, electric personal assistive mobility device, electric power-assisted bicycle, motorized skateboard or foot-scooter, or moped shall not travel between two lanes of traffic moving in the same direction, except where one lane is a separate turn lane or a mandatory turn lane.</p><p>Except as otherwise provided in this section, a person riding a bicycle, electric personal assistive mobility device, electric power-assisted bicycle, motorized skateboard or foot-scooter, or moped shall comply with all rules applicable to the driver of a motor vehicle when overtaking and passing.</p><p>1981, c. 585, § 46.1-229.2:1; 1989, c. 727; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>.</p>',6),s=[o];function n(c,l,p,d,h,g){return a(),i("div",null,s)}const m=e(t,[["render",n]]);export{f as __pageData,m as default};
