import{_ as e,o as s,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-919.1 Use of wireless telecommunications devices by persons driving school buses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-919.1.md","filePath":"vacode/46.2-919.1.md"}'),r={name:"vacode/46.2-919.1.md"},t=o('<h1 id="§-46-2-919-1-use-of-wireless-telecommunications-devices-by-persons-driving-school-buses" tabindex="-1">§ 46.2-919.1 Use of wireless telecommunications devices by persons driving school buses. <a class="header-anchor" href="#§-46-2-919-1-use-of-wireless-telecommunications-devices-by-persons-driving-school-buses" aria-label="Permalink to &quot;§ 46.2-919.1 Use of wireless telecommunications devices by persons driving school buses.&quot;">​</a></h1><p>No person shall use any wireless telecommunications device, whether handheld or otherwise, while driving a school bus, except in case of an emergency, or when the vehicle is lawfully parked and for the purposes of dispatching. Nothing in this section shall be construed to prohibit the use of (i) two-way radio devices or (ii) wireless telecommunications devices that are used hands free to allow live communication between the driver and school or public safety officials.</p><p>2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0234" target="_blank" rel="noreferrer">234</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0447" target="_blank" rel="noreferrer">447</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0295" target="_blank" rel="noreferrer">295</a>.</p>',3),a=[t];function n(c,l,d,h,p,u){return s(),i("div",null,a)}const v=e(r,[["render",n]]);export{f as __pageData,v as default};
