import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-932 Playing on highways; use of toy vehicle on highways, persons riding bicycles, electric personal assistive mobility devices, electric power-assisted bicycles, mopeds, etc., not to attach to vehicles; exception.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-932.md","filePath":"vacode/46.2-932.md"}'),s={name:"vacode/46.2-932.md"},o=a('<h1 id="§-46-2-932-playing-on-highways-use-of-toy-vehicle-on-highways-persons-riding-bicycles-electric-personal-assistive-mobility-devices-electric-power-assisted-bicycles-mopeds-etc-not-to-attach-to-vehicles-exception" tabindex="-1">§ 46.2-932 Playing on highways; use of toy vehicle on highways, persons riding bicycles, electric personal assistive mobility devices, electric power-assisted bicycles, mopeds, etc., not to attach to vehicles; exception. <a class="header-anchor" href="#§-46-2-932-playing-on-highways-use-of-toy-vehicle-on-highways-persons-riding-bicycles-electric-personal-assistive-mobility-devices-electric-power-assisted-bicycles-mopeds-etc-not-to-attach-to-vehicles-exception" aria-label="Permalink to &quot;§ 46.2-932 Playing on highways; use of toy vehicle on highways, persons riding bicycles, electric personal assistive mobility devices, electric power-assisted bicycles, mopeds, etc., not to attach to vehicles; exception.&quot;">​</a></h1><p>A. No person shall play on a highway, other than on the sidewalks thereof, within a city or town or on any part of a highway outside the limits of a city or town designated by the Commissioner of Highways exclusively for vehicular travel. No person shall use any toy vehicle on the roadway of any highway that (i) has a speed limit greater than 25 miles per hour, (ii) has more than two travel lanes, or (iii) is located outside a residence district as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-100/" target="_blank" rel="noreferrer">46.2-100</a>. The governing bodies of counties, cities, and towns may designate areas on highways under their control where play is permitted and may impose reasonable restrictions on play on such highways. Persons using such devices, except bicycles, electric personal assistive mobility devices, electric power-assisted bicycles, mopeds, and motorcycles, shall keep as near as safely practicable to the far right side or edge of the right traffic lane so that they will be proceeding in the same direction as other traffic.</p><p>No person riding on any bicycle, electric personal assistive mobility device, electric power-assisted bicycle, moped, roller skates, skateboards or other devices on wheels or runners, shall attach the same or himself to any vehicle on a highway.</p><p>B. Notwithstanding the provisions of subsection A of this section, the governing body of Arlington County may by ordinance permit the use of devices on wheels or runners on highways under such county&#39;s control, subject to such limitations and conditions as the governing body may deem necessary and reasonable.</p><p>Code 1950, § 46-248; 1958, c. 541, § 46.1-235; 1972, c. 817; 1973, c. 288; 1981, c. 585; 1989, c. 727; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0170" target="_blank" rel="noreferrer">170</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0973" target="_blank" rel="noreferrer">973</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0209" target="_blank" rel="noreferrer">209</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0366" target="_blank" rel="noreferrer">366</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>.</p>',5),r=[o];function n(c,l,h,g,p,d){return i(),t("div",null,r)}const v=e(s,[["render",n]]);export{f as __pageData,v as default};
