import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-941 Conditions precedent to issuance of summons for violation of parking ordinance; notice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-941.md","filePath":"vacode/46.2-941.md"}'),i={name:"vacode/46.2-941.md"},a=n('<h1 id="§-46-2-941-conditions-precedent-to-issuance-of-summons-for-violation-of-parking-ordinance-notice" tabindex="-1">§ 46.2-941 Conditions precedent to issuance of summons for violation of parking ordinance; notice. <a class="header-anchor" href="#§-46-2-941-conditions-precedent-to-issuance-of-summons-for-violation-of-parking-ordinance-notice" aria-label="Permalink to &quot;§ 46.2-941 Conditions precedent to issuance of summons for violation of parking ordinance; notice.&quot;">​</a></h1><p>Before any summons shall be issued for the prosecution of a violation of an ordinance of any county, city, or town regulating parking, the violator shall have been first notified by mail at his last known address or at the address shown for such violator on the records of the Department of Motor Vehicles, that he may pay the fine provided by law for such violation, within five days of receipt of such notice, and the authorized person issuing such summons shall be notified that the violator has failed to pay such fine within such time. The notice to the violator, required by the provisions of this section, shall be contained in an envelope bearing the words &quot;Law-Enforcement Notice&quot; stamped or printed on the face thereof in all capital letters, bold face type, no smaller than the print type size used for the primary address on the envelope. If &quot;window&quot; envelopes are used, the words &quot;Law-Enforcement Notice&quot; shall be clearly visible through the window of the envelope.</p><p>1968, c. 388, § 46.1-179.01; 1970, c. 510; 1978, c. 194; 1983, c. 329; 1989, c. 727; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0291" target="_blank" rel="noreferrer">291</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0323" target="_blank" rel="noreferrer">323</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0102" target="_blank" rel="noreferrer">102</a>.</p>',3),r=[a];function s(c,l,h,d,f,p){return o(),t("div",null,r)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
