import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 47.1-15.1 Additional prohibition on advertising; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/47.1-15.1.md","filePath":"vacode/47.1-15.1.md"}'),o={name:"vacode/47.1-15.1.md"},n=a('<h1 id="§-47-1-15-1-additional-prohibition-on-advertising-penalties" tabindex="-1">§ 47.1-15.1 Additional prohibition on advertising; penalties. <a class="header-anchor" href="#§-47-1-15-1-additional-prohibition-on-advertising-penalties" aria-label="Permalink to &quot;§ 47.1-15.1 Additional prohibition on advertising; penalties.&quot;">​</a></h1><p>A. A notary public shall not offer or provide legal advice on immigration or other legal matters, or represent any person in immigration proceedings, unless such notary public is authorized or licensed to practice law in the Commonwealth or is accredited pursuant to 8 C.F.R. § 292.2 to practice immigration law or represent persons in immigration proceedings.</p><p>B. A notary public shall not assume, use, or advertise the title of &quot;notario,&quot; &quot;notario publico,&quot; or &quot;licenciado,&quot; or a term in a language other than English that indicates in such language that the notary is authorized to provide legal advice or practice law, unless such notary public is authorized or licensed to practice law in Virginia.</p><p>C. Any person who violates the provisions of subsection B is subject to a civil penalty not to exceed $500 for a first violation and a civil penalty not to exceed $1,000 for a second or subsequent violation. All penalties arising under this section shall be recovered in a civil action brought by the Attorney General in the name of the Commonwealth and the proceeds shall be deposited into the Legal Aid Services Fund established in § <a href="http://law.lis.virginia.gov/vacode/17.1-278/" target="_blank" rel="noreferrer">17.1-278</a>.</p><p>D. Nothing in this section shall preempt or preclude additional civil, administrative, or criminal penalties authorized by law.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0544" target="_blank" rel="noreferrer">544</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>.</p>',6),r=[n];function s(l,c,d,p,h,u){return i(),t("div",null,r)}const _=e(o,[["render",s]]);export{v as __pageData,_ as default};
