import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 47.1-20 Fee agreements with employer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/47.1-20.md","filePath":"vacode/47.1-20.md"}'),o={name:"vacode/47.1-20.md"},n=a('<h1 id="§-47-1-20-fee-agreements-with-employer" tabindex="-1">§ 47.1-20 Fee agreements with employer. <a class="header-anchor" href="#§-47-1-20-fee-agreements-with-employer" aria-label="Permalink to &quot;§ 47.1-20 Fee agreements with employer.&quot;">​</a></h1><p>A. Any employer, as a condition of employment of a person who is a notary, may require the employee to perform notarial acts in the course of or in connection with such employment without charging the fee allowed by law for the performance of such acts.</p><p>B. Any employer may require a notary in his employment to surrender to such employer a fee, if charged, or any part thereof, provided that the notarial act for which the fee is charged is performed during the course of such employee&#39;s employment.</p><p>1980, c. 580; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0698" target="_blank" rel="noreferrer">698</a>.</p>',4),i=[n];function c(l,s,h,p,m,f){return r(),t("div",null,i)}const g=e(o,[["render",c]]);export{d as __pageData,g as default};
