import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 47.1-28 Willful misconduct a misdemeanor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/47.1-28.md","filePath":"vacode/47.1-28.md"}'),i={name:"vacode/47.1-28.md"},n=o('<h1 id="§-47-1-28-willful-misconduct-a-misdemeanor" tabindex="-1">§ 47.1-28 Willful misconduct a misdemeanor. <a class="header-anchor" href="#§-47-1-28-willful-misconduct-a-misdemeanor" aria-label="Permalink to &quot;§ 47.1-28 Willful misconduct a misdemeanor.&quot;">​</a></h1><p>A. Any notary who knowingly and willfully commits any official misconduct under Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/47.1-24/" target="_blank" rel="noreferrer">47.1-24</a> et seq.) of this title shall be guilty of a Class 3 misdemeanor.</p><p>B. Any employer of a notary who willfully induces such notary to commit official misconduct under Chapter 5 of this title shall be guilty of a Class 3 misdemeanor.</p><p>C. Any person who knowingly and willfully misrepresents on an application for commission as a notary whether they have been convicted of any felony under the laws of this Commonwealth, of any other state, or of the United States shall be guilty of a Class 1 misdemeanor.</p><p>1980, c. 580; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0814" target="_blank" rel="noreferrer">814</a>.</p>',5),l=[n];function r(s,c,d,m,f,h){return a(),t("div",null,l)}const p=e(i,[["render",r]]);export{_ as __pageData,p as default};
