import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 47.1-4 Qualification for appointment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/47.1-4.md","filePath":"vacode/47.1-4.md"}'),o={name:"vacode/47.1-4.md"},n=i('<h1 id="§-47-1-4-qualification-for-appointment" tabindex="-1">§ 47.1-4 Qualification for appointment. <a class="header-anchor" href="#§-47-1-4-qualification-for-appointment" aria-label="Permalink to &quot;§ 47.1-4 Qualification for appointment.&quot;">​</a></h1><p>To be qualified to be commissioned as a notary in the Commonwealth, each such person (i) shall be at least 18 years of age, (ii) shall be a legal resident of the United States, (iii) shall be able to read and write the English language, (iv) shall never have been convicted of a felony under the laws of the United States, the Commonwealth, or any other state, unless such person has been pardoned for such felony, has had his conviction vacated by the granting of a writ of actual innocence, or has had his rights restored, and (v) shall otherwise be in compliance with the provisions of this title. A nonresident of Virginia may register and be commissioned as a notary only if he is regularly employed in the Commonwealth and meets all of the requirements of this section. A member of the armed services of the United States shall be eligible to register and be commissioned as a notary notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-2800/" target="_blank" rel="noreferrer">2.2-2800</a>.</p><p>Code 1950, §§ 47-2, 47-3; 1972, c. 824; 1976, c. 559; 1979, c. 14; 1980, c. 580; 1983, c. 435; 1986, c. 561; 1989, c. 163; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0086" target="_blank" rel="noreferrer">86</a>.</p>',3),r=[n];function s(l,c,h,f,d,p){return a(),t("div",null,r)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
