import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 47.1-7 Additional requirements for performing electronic notarial acts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/47.1-7.md","filePath":"vacode/47.1-7.md"}'),a={name:"vacode/47.1-7.md"},o=i('<h1 id="§-47-1-7-additional-requirements-for-performing-electronic-notarial-acts" tabindex="-1">§ 47.1-7 Additional requirements for performing electronic notarial acts. <a class="header-anchor" href="#§-47-1-7-additional-requirements-for-performing-electronic-notarial-acts" aria-label="Permalink to &quot;§ 47.1-7 Additional requirements for performing electronic notarial acts.&quot;">​</a></h1><p>A. An applicant shall submit a registration form established by the Secretary for registering and being commissioned as an electronic notary public, which shall include:</p><ol><li><p>The applicant&#39;s full legal and official notary names;</p></li><li><p>A general description of the technology or technologies the registrant will use to create an electronic signature in performing official acts;</p></li><li><p>Certification of compliance to the Secretary of the Commonwealth with electronic notary standards developed in accordance with § <a href="http://law.lis.virginia.gov/vacode/47.1-6.1/" target="_blank" rel="noreferrer">47.1-6.1</a>; and</p></li><li><p>The electronic mail address of the registrant.</p></li></ol><p>B. The registration form shall (i) be signed by the applicant using the electronic signature described in the form; (ii) include any decrypting instructions, codes, keys, or software that allow the registration to be read; and (iii) be transmitted electronically to the Secretary.</p><p>C. Nothing herein shall be construed to prevent an electronic notary from using updated technology or technologies during the term of the commission; however, the electronic notary shall notify the Secretary electronically within 90 days of installation or use of such updated technology or technologies and provide a brief description thereof.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0160" target="_blank" rel="noreferrer">160</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0731" target="_blank" rel="noreferrer">731</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>.</p>',6),n=[o];function l(c,s,h,d,p,f){return r(),t("div",null,n)}const _=e(a,[["render",l]]);export{m as __pageData,_ as default};
