import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 48-7 Houses and contents are nuisances subject to abatement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/48-7.md","filePath":"vacode/48-7.md"}'),s={name:"vacode/48-7.md"},i=n('<h1 id="§-48-7-houses-and-contents-are-nuisances-subject-to-abatement" tabindex="-1">§ 48-7 Houses and contents are nuisances subject to abatement. <a class="header-anchor" href="#§-48-7-houses-and-contents-are-nuisances-subject-to-abatement" aria-label="Permalink to &quot;§ 48-7 Houses and contents are nuisances subject to abatement.&quot;">​</a></h1><p>Any person who shall knowingly erect, establish, continue, maintain, use, own, occupy or lease any building, erection, place, or area used for the purpose of lewdness, assignation, prostitution, or activities of a criminal street gang, as criminal street gang is defined in § <a href="http://law.lis.virginia.gov/vacode/18.2-46.1/" target="_blank" rel="noreferrer">18.2-46.1</a> in the Commonwealth is guilty of a nuisance, and the building, erection, place, or area, the ground itself, in or upon which such lewdness, assignation, prostitution, or criminal street gang activity is conducted, permitted, or carried on, continued, or exists, and the furniture, fixtures, musical instruments and contents are also declared a nuisance, and shall be enjoined and abated as hereinafter provided.</p><p>Code 1919, § 1521; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>.</p>',3),o=[i];function r(c,d,l,u,h,p){return a(),t("div",null,o)}const f=e(s,[["render",r]]);export{g as __pageData,f as default};
