import{_ as o,o as i,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 5.1-113 Duration of certificates, registrations, etc.; suspension, revocation, or amendment; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/5.1-113.md","filePath":"vacode/5.1-113.md"}'),a={name:"vacode/5.1-113.md"},s=e("h1",{id:"§-5-1-113-duration-of-certificates-registrations-etc-suspension-revocation-or-amendment-penalties",tabindex:"-1"},[t("§ 5.1-113 Duration of certificates, registrations, etc.; suspension, revocation, or amendment; penalties. "),e("a",{class:"header-anchor",href:"#§-5-1-113-duration-of-certificates-registrations-etc-suspension-revocation-or-amendment-penalties","aria-label":'Permalink to "§ 5.1-113 Duration of certificates, registrations, etc.; suspension, revocation, or amendment; penalties."'},"​")],-1),n=e("p",null,"Certificates, permits, registrations, and licenses issued under the provisions of this chapter shall be effective from the dates specified therein and shall remain in effect until terminated as herein provided. The Commission may at any time, by its order duly entered after hearing held after notice to the holder of any such certificate, permit, registration, or license and an opportunity to such holder to be heard at which it shall be proved that such holder has willfully made any misrepresentation of a material fact in obtaining such certificate, permit, registration, or license, or has willfully violated or refused to observe the laws of this Commonwealth touching such certificate, permit, registration, or license, or any of the terms of his certificate, permit, registration, or license, or any of the Commission's proper orders, rules, or regulations, impose a penalty not exceeding $1,000, which may be collected by the process of the Commission as provided by law; or the Commission may suspend, revoke, alter, or amend any such certificate, permit, registration, or license for any of the causes set forth above. But no such certificate, permit, registration, or license shall be revoked, altered, or amended (except upon application of the holder thereof) unless the holder thereof shall willfully fail to comply, within a reasonable time to be fixed by the Commission, with the lawful order of the Commission or with the lawful rule or regulation of the Commission, or with the term, condition, or limitation of such certificate, permit, registration, or license, found by the Commission to have been violated by such holder.",-1),c=e("p",null,"Proceedings for the imposition of any penalty provided for in this section may be commenced upon the complaint of any person or upon the Commission's own initiative.",-1),l=e("p",null,"From any order of the Commission suspending, revoking, altering or amending any certificate, permit, registration, or license, the holder thereof shall have the right of appeal to the Supreme Court of Virginia, as a matter of right, as in other cases of appeals from the Commission.",-1),h=e("p",null,[t("Code 1950, § 56-167; 1970, c. 708; 2017, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0793",target:"_blank",rel:"noreferrer"},"793"),t(".")],-1),d=[s,n,c,l,h];function m(f,p,u,g,y,v){return i(),r("div",null,d)}const C=o(a,[["render",m]]);export{b as __pageData,C as default};
