import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 5.1-2.19 Same; governing body; officers and agents; powers generally.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/5.1-2.19.md","filePath":"vacode/5.1-2.19.md"}'),o={name:"vacode/5.1-2.19.md"},n=t('<h1 id="§-5-1-2-19-same-governing-body-officers-and-agents-powers-generally" tabindex="-1">§ 5.1-2.19 Same; governing body; officers and agents; powers generally. <a class="header-anchor" href="#§-5-1-2-19-same-governing-body-officers-and-agents-powers-generally" aria-label="Permalink to &quot;§ 5.1-2.19 Same; governing body; officers and agents; powers generally.&quot;">​</a></h1><p>The agreement shall specify the composition of the governing body of the joint agency or authority created under § <a href="http://law.lis.virginia.gov/vacode/5.1-2.18/" target="_blank" rel="noreferrer">5.1-2.18</a>; provided, however, that the membership thereof from this Commonwealth shall be the same as the membership of the Board created by § <a href="http://law.lis.virginia.gov/vacode/5.1-2.1/" target="_blank" rel="noreferrer">5.1-2.1</a>. Each joint authority shall select officers for such terms as are fixed by the agreement and shall have the power to employ such agents and employees as it may require and as are reasonably necessary in carrying out the purposes of this chapter. Subject to the limitations and restrictions hereinafter set forth, such joint authority shall have the powers set forth in § <a href="http://law.lis.virginia.gov/vacode/5.1-2.2:1/" target="_blank" rel="noreferrer">5.1-2.2:1</a>.</p><p>1980, c. 750.</p>',3),s=[n];function i(h,l,c,d,f,g){return a(),r("div",null,s)}const m=e(o,[["render",i]]);export{_ as __pageData,m as default};
