import{_ as a,o as n,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 5.1-30.2 Creation and management of Virginia Airports Revolving Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/5.1-30.2.md","filePath":"vacode/5.1-30.2.md"}'),o={name:"vacode/5.1-30.2.md"},r=e("h1",{id:"§-5-1-30-2-creation-and-management-of-virginia-airports-revolving-fund",tabindex:"-1"},[t("§ 5.1-30.2 Creation and management of Virginia Airports Revolving Fund. "),e("a",{class:"header-anchor",href:"#§-5-1-30-2-creation-and-management-of-virginia-airports-revolving-fund","aria-label":'Permalink to "§ 5.1-30.2 Creation and management of Virginia Airports Revolving Fund."'},"​")],-1),s=e("p",null,'There shall be set apart as a permanent and perpetual fund, to be known as the "Virginia Airports Revolving Fund," sums appropriated to the Fund by the General Assembly, all receipts by the Fund from loans made by it to local governments, all income from the investment of moneys held in the Fund, and any other sums designated for deposit to the Fund from any source public or private, including without limitation any federal grants, awards or other forms of assistance received by the Commonwealth that are eligible for deposit therein under federal law. The Authority shall administer and manage the Fund, and establish the interest rates and repayment terms of such loans as provided in this chapter, in accordance with a memorandum of agreement with the Board. The Board shall direct the distribution of loans from the Fund to particular local governments. Consistent with this chapter, the Board shall, after consultation with all interested parties, develop a guidance document governing project eligibility and project priority criteria. In order to carry out the administration and management of the Fund, the Authority, in consultation with the Board, is granted the power to employ officers, employees, agents, advisers and consultants, including, without limitation, attorneys, financial advisers, engineers and other technical advisers and public accountants and, the provisions of any other law to the contrary notwithstanding, to determine their duties and compensation without the approval of any other agency or instrumentality. The Authority may disburse from the Fund its reasonable costs and expenses incurred in the administration and management of the Fund and a reasonable fee to be approved by the Board for its management services.',-1),d=e("p",null,[t("1999, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0897",target:"_blank",rel:"noreferrer"},"897"),t(".")],-1),l=[r,s,d];function h(c,m,p,u,f,g){return n(),i("div",null,l)}const _=a(o,[["render",h]]);export{y as __pageData,_ as default};
