import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 50-73.115 Statement of dissociation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/50-73.115.md","filePath":"vacode/50-73.115.md"}'),o={name:"vacode/50-73.115.md"},r=i('<h1 id="§-50-73-115-statement-of-dissociation" tabindex="-1">§ 50-73.115 Statement of dissociation. <a class="header-anchor" href="#§-50-73-115-statement-of-dissociation" aria-label="Permalink to &quot;§ 50-73.115 Statement of dissociation.&quot;">​</a></h1><p>A. A dissociated partner named as a partner in a filed statement of partnership authority or in a list maintained by an agent pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/50-73.93/" target="_blank" rel="noreferrer">50-73.93</a> or a partnership that has filed a statement of partnership authority that has not been canceled may file a statement of dissociation stating the name of the partnership, the identification number issued by the Commission to the partnership, and that the partner is dissociated from the partnership.</p><p>B. A statement of dissociation is a limitation on the authority of a dissociated partner for the purposes of subsections D and E of § <a href="http://law.lis.virginia.gov/vacode/50-73.93/" target="_blank" rel="noreferrer">50-73.93</a>.</p><p>C. For the purposes of subdivision A 3 of § <a href="http://law.lis.virginia.gov/vacode/50-73.113/" target="_blank" rel="noreferrer">50-73.113</a> and subdivision B 3 of § <a href="http://law.lis.virginia.gov/vacode/50-73.114/" target="_blank" rel="noreferrer">50-73.114</a>, a person not a partner is deemed to have notice of the dissociation 90 days after the statement of dissociation is filed.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0292" target="_blank" rel="noreferrer">292</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0567" target="_blank" rel="noreferrer">567</a>.</p>',5),n=[r];function s(d,c,f,h,p,l){return t(),a("div",null,n)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
