import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 50-73.11:3 Conversion of general partnership to limited partnership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/50-73.11:3.md","filePath":"vacode/50-73.11:3.md"}'),a={name:"vacode/50-73.11:3.md"},n=i('<h1 id="§-50-73-11-3-conversion-of-general-partnership-to-limited-partnership" tabindex="-1">§ 50-73.11:3 Conversion of general partnership to limited partnership. <a class="header-anchor" href="#§-50-73-11-3-conversion-of-general-partnership-to-limited-partnership" aria-label="Permalink to &quot;§ 50-73.11:3 Conversion of general partnership to limited partnership.&quot;">​</a></h1><p>A. A domestic or foreign general partnership may convert to a limited partnership pursuant to this section.</p><p>B. The terms and conditions of a conversion of a general partnership to a limited partnership shall be approved by the partners in the manner provided in the partnership&#39;s partnership agreement for amendments to the partnership agreement or, if no such provision is made in the partnership agreement, by all of the partners.</p><p>C. After the conversion is approved by the partners, the general partnership shall file a certificate of limited partnership that meets the requirements of § <a href="http://law.lis.virginia.gov/vacode/50-73.11/" target="_blank" rel="noreferrer">50-73.11</a> and includes the following:</p><ol><li><p>The name of the former general partnership and the identification number issued by the Commission to the general partnership, if any;</p></li><li><p>The jurisdiction under whose law the general partnership was formed immediately prior to the filing of the certificate of limited partnership;</p></li><li><p>If the former general partnership is registered with the Commission as a registered limited liability partnership, a statement to that effect;</p></li><li><p>A statement that the conversion of the general partnership to a limited partnership was approved by the partners in accordance with the provisions of subsection B.</p></li></ol><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0631" target="_blank" rel="noreferrer">631</a>.</p>',6),o=[n];function s(p,h,l,d,c,f){return t(),r("div",null,o)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
