import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 50-73.126 Conversion of limited partnership to partnership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/50-73.126.md","filePath":"vacode/50-73.126.md"}'),i={name:"vacode/50-73.126.md"},n=a('<h1 id="§-50-73-126-conversion-of-limited-partnership-to-partnership" tabindex="-1">§ 50-73.126 Conversion of limited partnership to partnership. <a class="header-anchor" href="#§-50-73-126-conversion-of-limited-partnership-to-partnership" aria-label="Permalink to &quot;§ 50-73.126 Conversion of limited partnership to partnership.&quot;">​</a></h1><p>A. A limited partnership may be converted to a partnership pursuant to this section.</p><p>B. Notwithstanding a provision to the contrary in a limited partnership agreement, the terms and conditions of a conversion of a limited partnership to a partnership shall be approved by all of the partners.</p><p>C. After the conversion is approved by the partners, the limited partnership shall cancel its certificate of limited partnership.</p><p>D. The conversion takes effect when the certificate of limited partnership is canceled.</p><p>E. A limited partner who becomes a general partner as a result of the conversion remains liable only as a limited partner for an obligation incurred by the limited partnership before the conversion takes effect. Subject to § <a href="http://law.lis.virginia.gov/vacode/50-73.96/" target="_blank" rel="noreferrer">50-73.96</a>, the partner is liable as a general partner for an obligation of the partnership incurred after the conversion takes effect.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0292" target="_blank" rel="noreferrer">292</a>.</p>',7),o=[n];function s(p,h,c,l,d,f){return t(),r("div",null,o)}const v=e(i,[["render",s]]);export{m as __pageData,v as default};
