import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 50-73.91 Partner agent of partnership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/50-73.91.md","filePath":"vacode/50-73.91.md"}'),n={name:"vacode/50-73.91.md"},i=a('<h1 id="§-50-73-91-partner-agent-of-partnership" tabindex="-1">§ 50-73.91 Partner agent of partnership. <a class="header-anchor" href="#§-50-73-91-partner-agent-of-partnership" aria-label="Permalink to &quot;§ 50-73.91 Partner agent of partnership.&quot;">​</a></h1><p>Subject to the effect of a statement of partnership authority under § <a href="http://law.lis.virginia.gov/vacode/50-73.93/" target="_blank" rel="noreferrer">50-73.93</a>:</p><ol><li><p>Each partner is an agent of the partnership for the purpose of its business. An act of a partner, including the execution of an instrument in the partnership name, for apparently carrying on in the ordinary course the partnership business or business of the kind carried on by the partnership binds the partnership, unless the partner had no authority to act for the partnership in the particular matter and the person with whom the partner was dealing knew or had received a notification that the partner lacked authority.</p></li><li><p>An act of a partner which is not apparently for carrying on in the ordinary course the partnership business or business of the kind carried on by the partnership binds the partnership only if the act was authorized by the other partners.</p></li></ol><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0292" target="_blank" rel="noreferrer">292</a>.</p>',4),o=[i];function s(p,h,c,d,f,l){return t(),r("div",null,o)}const g=e(n,[["render",s]]);export{u as __pageData,g as default};
