import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1117 Service retirement of participating full-time employees receiving disability benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1117.md","filePath":"vacode/51.1-1117.md"}'),a={name:"vacode/51.1-1117.md"},n=r('<h1 id="§-51-1-1117-service-retirement-of-participating-full-time-employees-receiving-disability-benefits" tabindex="-1">§ 51.1-1117 Service retirement of participating full-time employees receiving disability benefits. <a class="header-anchor" href="#§-51-1-1117-service-retirement-of-participating-full-time-employees-receiving-disability-benefits" aria-label="Permalink to &quot;§ 51.1-1117 Service retirement of participating full-time employees receiving disability benefits.&quot;">​</a></h1><p>A. A participating full-time employee receiving disability benefits who is a vested member of the retirement system shall be eligible for service retirement under any provision of this title for which the employee is otherwise eligible. Such employee shall be authorized to elect any option for the payment of his retirement allowance provided under subsection A of § <a href="http://law.lis.virginia.gov/vacode/51.1-165/" target="_blank" rel="noreferrer">51.1-165</a> for which the employee is otherwise eligible.</p><p>B. The average final compensation of any participating full-time employee taking a service retirement under any provision of this title shall be equal to his creditable compensation on the date of the commencement of the disability increased by an amount recommended by the program actuary and approved by the Board, from the date of the commencement of the disability to the date of retirement.</p><p>C. The creditable service of a participating full-time employee taking service retirement pursuant to this section shall include periods during which the employee received disability benefits.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0098" target="_blank" rel="noreferrer">98</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>.</p>',5),o=[n];function l(s,c,p,f,m,h){return i(),t("div",null,o)}const b=e(a,[["render",l]]);export{g as __pageData,b as default};
