import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 51.1-1123 Supplemental long-term disability benefit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1123.md","filePath":"vacode/51.1-1123.md"}'),a={name:"vacode/51.1-1123.md"},l=r('<h1 id="§-51-1-1123-supplemental-long-term-disability-benefit" tabindex="-1">§ 51.1-1123 Supplemental long-term disability benefit. <a class="header-anchor" href="#§-51-1-1123-supplemental-long-term-disability-benefit" aria-label="Permalink to &quot;§ 51.1-1123 Supplemental long-term disability benefit.&quot;">​</a></h1><p>A. Supplemental long-term disability benefits for participating employees shall commence upon the expiration of the maximum period for which the participating employee is eligible to receive short-term disability benefits under § <a href="http://law.lis.virginia.gov/vacode/51.1-1121/" target="_blank" rel="noreferrer">51.1-1121</a>.</p><p>B. Except as provided in subsection D and § <a href="http://law.lis.virginia.gov/vacode/51.1-1131/" target="_blank" rel="noreferrer">51.1-1131</a>, supplemental long-term disability benefits shall provide income replacement in an amount equal to 60 percent of a participating employee&#39;s creditable compensation.</p><p>C. Creditable compensation during periods an employee receives supplemental long-term disability benefits shall (i) not include salary increases awarded during the period covered by long-term disability benefits and (ii) be increased annually by an amount recommended by the program actuary and approved by the Board.</p><p>D. An employee&#39;s disability credits shall be used, on a day-for-day basis, to extend the period an employee receives supplemental long-term disability benefits paid at 100 percent of replacement of creditable compensation.</p><p>E. Payments of supplemental long-term disability benefits payable under this article shall be reduced by an amount equal to any benefits paid to the employee under the Act, for which the employee is entitled to receive under the Act, excluding any benefit for medical, legal or rehabilitation expenses.</p><p>F. Supplemental long-term disability benefits shall be payable only during periods of total and partial disability.</p><p>G. Unless otherwise directed, to be eligible for benefits under this section, the employee must apply for Social Security disability benefits.</p><p>H. An employee who is approved for disability benefits on or after the date that is five years prior to his normal retirement date shall be eligible for five years of disability benefits before the employee is required to retire under a service retirement. The five years includes short-term disability and long-term disability.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0889" target="_blank" rel="noreferrer">889</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0013" target="_blank" rel="noreferrer">13</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0098" target="_blank" rel="noreferrer">98</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0419" target="_blank" rel="noreferrer">419</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0404" target="_blank" rel="noreferrer">404</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0841" target="_blank" rel="noreferrer">841</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0750" target="_blank" rel="noreferrer">750</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>.</p>',10),n=[l];function o(s,p,d,g,b,c){return i(),t("div",null,n)}const m=e(a,[["render",o]]);export{h as __pageData,m as default};
