import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1127 Cessation of supplemental disability benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1127.md","filePath":"vacode/51.1-1127.md"}'),r={name:"vacode/51.1-1127.md"},n=a('<h1 id="§-51-1-1127-cessation-of-supplemental-disability-benefits" tabindex="-1">§ 51.1-1127 Cessation of supplemental disability benefits. <a class="header-anchor" href="#§-51-1-1127-cessation-of-supplemental-disability-benefits" aria-label="Permalink to &quot;§ 51.1-1127 Cessation of supplemental disability benefits.&quot;">​</a></h1><p>Supplemental disability benefits shall cease to be paid to a participating employee upon the first to occur of the following:</p><ol><li><p>The end of the period of supplemental disability coverage as provided in subsection G of § <a href="http://law.lis.virginia.gov/vacode/51.1-1121/" target="_blank" rel="noreferrer">51.1-1121</a> or subsection F of § <a href="http://law.lis.virginia.gov/vacode/51.1-1123/" target="_blank" rel="noreferrer">51.1-1123</a>;</p></li><li><p>The date of death of the participating employee;</p></li><li><p>On the date benefits under the Act cease to be paid, if the participating employee is a member of the retirement system and is receiving benefits under the Act on his normal retirement date;</p></li><li><p>On the date benefits under the Act cease to be paid, if the participating employee is a not a member of the retirement system and is receiving benefits under the Act on the date he attains age 65;</p></li><li><p>On his normal retirement date, if the participating employee is a member of the retirement system and is no longer receiving benefits under the Act on his normal retirement date;</p></li><li><p>On the date the employee attains age 65, if the participating employee is a not a member of the retirement system and is no longer receiving benefits under the Act; or</p></li><li><p>The effective date of the participating employee&#39;s service retirement under any provision of this title.</p></li></ol><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0417" target="_blank" rel="noreferrer">417</a>.</p>',4),o=[n];function s(l,p,c,f,h,d){return t(),i("div",null,o)}const b=e(r,[["render",s]]);export{g as __pageData,b as default};
