import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 51.1-1128 Service retirement of participating full-time employees receiving supplemental disability benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1128.md","filePath":"vacode/51.1-1128.md"}'),a={name:"vacode/51.1-1128.md"},n=r('<h1 id="§-51-1-1128-service-retirement-of-participating-full-time-employees-receiving-supplemental-disability-benefits" tabindex="-1">§ 51.1-1128 Service retirement of participating full-time employees receiving supplemental disability benefits. <a class="header-anchor" href="#§-51-1-1128-service-retirement-of-participating-full-time-employees-receiving-supplemental-disability-benefits" aria-label="Permalink to &quot;§ 51.1-1128 Service retirement of participating full-time employees receiving supplemental disability benefits.&quot;">​</a></h1><p>A. Upon the cessation of benefits payable under the Act, a participating full-time employee may take service retirement under any provision of this title for which the employee is otherwise eligible. Such employee shall be authorized to elect any option for the payment of his retirement allowance provided under subsection A of § <a href="http://law.lis.virginia.gov/vacode/51.1-165/" target="_blank" rel="noreferrer">51.1-165</a>.</p><p>B. The employee&#39;s average final compensation shall be equal to his creditable compensation on the date of the commencement of the disability increased by an amount recommended by the actuary of the Virginia Retirement System, and approved by the Board, from the date of the commencement of the disability to the date of retirement.</p><p>C. The creditable service of a participating full-time employee taking service retirement pursuant to this section shall include periods during which the employee received supplemental disability benefits, provided that such creditable service shall not include periods for which (i) the employee received supplemental short-term disability benefits, (ii) the employer did not report such creditable service to the retirement system, and (iii) the employee did not purchase such creditable service.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>.</p>',5),l=[n];function o(s,c,p,m,h,d){return i(),t("div",null,l)}const b=e(a,[["render",o]]);export{u as __pageData,b as default};
