import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 51.1-1132 Health insurance coverage during disability absences.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1132.md","filePath":"vacode/51.1-1132.md"}'),i={name:"vacode/51.1-1132.md"},n=t('<h1 id="§-51-1-1132-health-insurance-coverage-during-disability-absences" tabindex="-1">§ 51.1-1132 Health insurance coverage during disability absences. <a class="header-anchor" href="#§-51-1-1132-health-insurance-coverage-during-disability-absences" aria-label="Permalink to &quot;§ 51.1-1132 Health insurance coverage during disability absences.&quot;">​</a></h1><p>A. Participating employees enrolled in a health insurance plan established pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a> shall continue to be covered during periods of short-term disability and shall have the option of continuing to be covered by such plan during periods of absence covered by long-term disability benefits.</p><p>B. The Commonwealth shall pay the employer&#39;s share of the cost of health insurance coverage under such plan for participating employees and for the families or dependents of such employees during periods the employee is receiving short-term disability benefits to the same extent as for other state employees covered by such plan.</p><p>C. Participating employees enrolled in such plan established pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a> shall have the option of continuing to be covered under such plan, and shall pay the full cost for coverage under such plan for themselves and for their families and dependents during periods the employee is receiving long-term disability benefits. However, for an employee as defined in § <a href="http://law.lis.virginia.gov/vacode/51.1-201/" target="_blank" rel="noreferrer">51.1-201</a> who is receiving long-term disability benefits for a work-related disability pursuant to Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/51.1-1119/" target="_blank" rel="noreferrer">51.1-1119</a> et seq.) of Chapter 11, the Commonwealth shall continue to pay the employer&#39;s share of the cost of health insurance coverage under such plan for the participating employee and for his family and dependents until such time as the employee is approved for continued health insurance coverage as provided under Chapter 4 (§ <a href="http://law.lis.virginia.gov/vacode/9.1-400/" target="_blank" rel="noreferrer">9.1-400</a> et seq.) of Title 9.1.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0090" target="_blank" rel="noreferrer">90</a>.</p>',5),o=[n];function s(l,c,h,d,p,g){return a(),r("div",null,o)}const v=e(i,[["render",s]]);export{u as __pageData,v as default};
