import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 51.1-1133 Life and accident insurance coverage during disability absences.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1133.md","filePath":"vacode/51.1-1133.md"}'),n={name:"vacode/51.1-1133.md"},r=t('<h1 id="§-51-1-1133-life-and-accident-insurance-coverage-during-disability-absences" tabindex="-1">§ 51.1-1133 Life and accident insurance coverage during disability absences. <a class="header-anchor" href="#§-51-1-1133-life-and-accident-insurance-coverage-during-disability-absences" aria-label="Permalink to &quot;§ 51.1-1133 Life and accident insurance coverage during disability absences.&quot;">​</a></h1><p>A. Participating full-time employees participating in a group life and accident insurance program established pursuant to Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/51.1-500/" target="_blank" rel="noreferrer">51.1-500</a> et seq.) of this title shall continue to participate in such program during periods of absence covered by short-term and long-term disability benefits.</p><p>B. During periods of absence covered by short-term disability benefits, the amount of the life insurance benefit shall be based on the annual salary of the participating full-time employee at the commencement of the disability and shall be adjusted to include salary increases awarded during the period covered by short-term disability benefits.</p><p>C. During periods of absence covered by long-term disability benefits, the amount of the life insurance benefit shall be based on the annual salary of the participating employee at the commencement of the disability. Such amount shall (i) not include salary increases awarded during the period covered by long-term disability benefits and (ii) be increased annually by an amount recommended by the program actuary and approved by the Board.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0144" target="_blank" rel="noreferrer">144</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0098" target="_blank" rel="noreferrer">98</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>.</p>',5),s=[r];function o(c,l,d,b,p,f){return a(),i("div",null,s)}const u=e(n,[["render",o]]);export{h as __pageData,u as default};
