import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1135.2 Board authorized to provide long-term care insurance and benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1135.2.md","filePath":"vacode/51.1-1135.2.md"}'),n={name:"vacode/51.1-1135.2.md"},i=t('<h1 id="§-51-1-1135-2-board-authorized-to-provide-long-term-care-insurance-and-benefits" tabindex="-1">§ 51.1-1135.2 Board authorized to provide long-term care insurance and benefits. <a class="header-anchor" href="#§-51-1-1135-2-board-authorized-to-provide-long-term-care-insurance-and-benefits" aria-label="Permalink to &quot;§ 51.1-1135.2 Board authorized to provide long-term care insurance and benefits.&quot;">​</a></h1><p>A. For purposes of this section, &quot;participating employee&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/51.1-1100/" target="_blank" rel="noreferrer">51.1-1100</a>.</p><p>B. The Board is authorized to develop, implement, and administer a long-term care insurance program for participating employees that includes, among other elements, provisions under which a person may purchase continuing coverage if he ceases to be a participating employee. The Board may contract for and purchase such long-term care insurance or may self-insure long-term care benefits or may use such other actuarially sound funding necessary to effectuate such long-term care insurance and benefits.</p><p>C. The costs of providing long-term care benefits for participating employees shall be paid by state agencies from funds as shall be appropriated by law to state agencies. State agencies shall pay to the Board from such funds contribution amounts, to be determined by the Board, to provide the Board with such funds as shall be required from time to time to (i) obtain and maintain long-term care insurance and benefits for participating employees, and (ii) administer the long-term care insurance program, including providing case management and cost containment programs. Contributions shall be deposited in the Disability Insurance Trust Fund established under § <a href="http://law.lis.virginia.gov/vacode/51.1-1140/" target="_blank" rel="noreferrer">51.1-1140</a>.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0663" target="_blank" rel="noreferrer">663</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0030" target="_blank" rel="noreferrer">30</a>.</p>',5),o=[i];function s(c,d,l,p,h,u){return a(),r("div",null,o)}const f=e(n,[["render",s]]);export{g as __pageData,f as default};
