import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1140 Funding of program; Disability Insurance Trust Fund established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1140.md","filePath":"vacode/51.1-1140.md"}'),n={name:"vacode/51.1-1140.md"},r=t('<h1 id="§-51-1-1140-funding-of-program-disability-insurance-trust-fund-established" tabindex="-1">§ 51.1-1140 Funding of program; Disability Insurance Trust Fund established. <a class="header-anchor" href="#§-51-1-1140-funding-of-program-disability-insurance-trust-fund-established" aria-label="Permalink to &quot;§ 51.1-1140 Funding of program; Disability Insurance Trust Fund established.&quot;">​</a></h1><p>A. The costs of providing sick leave, family and personal leave, and short-term disability benefits shall be paid by state agencies from funds as shall be appropriated by law to state agencies.</p><p>B. State agencies shall pay to the Board, from funds as shall be appropriated by law to state agencies, contribution amounts, to be determined by the Board, to provide the Board with such funds as shall be required from time to time to (i) obtain and maintain long-term disability insurance policies under this chapter, and (ii) administer the Program, including providing case management and cost containment programs. Contributions shall be deposited in the Disability Insurance Trust Fund.</p><p>C. There is hereby established the Disability Insurance Trust Fund. The costs incurred by the Board in providing policies of long-term disability insurance and administering the Program and in administering the long-term care insurance program established under § <a href="http://law.lis.virginia.gov/vacode/51.1-1135.2/" target="_blank" rel="noreferrer">51.1-1135.2</a>, including the provision of case management and cost containment programs, shall be withdrawn from time to time by the Board from the Disability Insurance Trust Fund. The funds of the Disability Insurance Trust Fund shall be deemed separate and independent trust funds, shall be segregated from all other funds of the Commonwealth, and shall be invested and administered solely in the interests of the participating employees and beneficiaries thereof. Neither the General Assembly nor any public officer, employee, or agency shall use or authorize the use of such trust funds for any purpose other than as provided in law for benefits, refunds, and administrative expenses, including but not limited to legislative oversight of the Disability Insurance Trust Fund.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0663" target="_blank" rel="noreferrer">663</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>.</p>',5),s=[r];function o(d,l,h,u,c,p){return a(),i("div",null,s)}const m=e(n,[["render",o]]);export{g as __pageData,m as default};
