import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.1-1155 Short-term disability benefit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1155.md","filePath":"vacode/51.1-1155.md"}'),a={name:"vacode/51.1-1155.md"},o=r('<h1 id="§-51-1-1155-short-term-disability-benefit" tabindex="-1">§ 51.1-1155 Short-term disability benefit. <a class="header-anchor" href="#§-51-1-1155-short-term-disability-benefit" aria-label="Permalink to &quot;§ 51.1-1155 Short-term disability benefit.&quot;">​</a></h1><p>A. Except as provided in subsection B of § <a href="http://law.lis.virginia.gov/vacode/51.1-1153/" target="_blank" rel="noreferrer">51.1-1153</a>, short-term disability benefits for participating employees shall commence upon the expiration of a seven-calendar-day waiting period. The waiting period shall commence the first day of a disability or of maternity leave. If an employee returns to work for one day or less during the seven-calendar-day waiting period but cannot continue to work, the periods worked shall not be considered to have interrupted the seven-calendar-day waiting period. Additionally, the seven-calendar-day waiting period shall not be considered to be interrupted if the employee works 20 hours or less during the waiting period. Short-term disability benefits payable as the result of a catastrophic disability or major chronic condition shall not require a waiting period.</p><p>B. Except as provided in § <a href="http://law.lis.virginia.gov/vacode/51.1-1171/" target="_blank" rel="noreferrer">51.1-1171</a>, short-term disability coverage shall provide income replacement for (i) 60 percent of a participating employee&#39;s creditable compensation for the first 60 months of continuous service and (ii) thereafter, a percentage of a participating employee&#39;s creditable compensation during the periods specified below, based on the number of months of continuous service attained by an employee who is disabled, on maternity leave, or takes periodic absences due to a major chronic condition, as determined by the Board or its designee, as follows:</p><table><thead><tr><th></th><th></th><th></th><th></th><th></th></tr></thead><tbody><tr><td>a</td><td></td><td>Work Days of 100%</td><td>Work Days of 80%</td><td>Work Days of 60%</td></tr><tr><td>b</td><td>Months of</td><td>Replacement of</td><td>Replacement of</td><td>Replacement of</td></tr><tr><td>c</td><td>Continuous</td><td>Creditable</td><td>Creditable</td><td>Creditable</td></tr><tr><td>d</td><td>Service</td><td>Compensation</td><td>Compensation</td><td>Compensation</td></tr><tr><td>e</td><td>60-119</td><td>25</td><td>25</td><td>75</td></tr><tr><td>f</td><td>120-179</td><td>25</td><td>50</td><td>50</td></tr><tr><td>g</td><td>180 or more</td><td>25</td><td>75</td><td>25</td></tr></tbody></table><p>C. Creditable compensation during periods an employee receives short-term disability benefits shall include salary increases awarded during the period covered by short-term disability benefits.</p><p>D. Short-term disability benefits shall be payable only during periods of (i) total disability, (ii) partial disability, (iii) maternity leave, or (iv) periodic absences due to a major chronic condition as defined by the Board or its designee.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0463" target="_blank" rel="noreferrer">463</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0356" target="_blank" rel="noreferrer">356</a>.</p>',7),d=[o];function n(s,l,c,p,h,b){return t(),i("div",null,d)}const g=e(a,[["render",n]]);export{m as __pageData,g as default};
