import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 51.1-1165 Supplemental long-term disability benefit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1165.md","filePath":"vacode/51.1-1165.md"}'),l={name:"vacode/51.1-1165.md"},n=a('<h1 id="§-51-1-1165-supplemental-long-term-disability-benefit" tabindex="-1">§ 51.1-1165 Supplemental long-term disability benefit. <a class="header-anchor" href="#§-51-1-1165-supplemental-long-term-disability-benefit" aria-label="Permalink to &quot;§ 51.1-1165 Supplemental long-term disability benefit.&quot;">​</a></h1><p>A. Supplemental long-term disability benefits for participating employees shall commence upon the expiration of the maximum period for which the participating employee is eligible to receive short-term disability benefits under § <a href="http://law.lis.virginia.gov/vacode/51.1-1163/" target="_blank" rel="noreferrer">51.1-1163</a>.</p><p>B. Except as provided in § <a href="http://law.lis.virginia.gov/vacode/51.1-1171/" target="_blank" rel="noreferrer">51.1-1171</a>, supplemental long-term disability benefits shall provide income replacement in an amount equal to 60 percent of a participating employee&#39;s creditable compensation.</p><p>C. Creditable compensation during periods an employee receives supplemental long-term disability benefits shall not include salary increases awarded during the period covered by long-term disability benefits.</p><p>D. Payments of supplemental long-term disability benefits payable under this article shall be reduced by an amount equal to any benefits paid to the employee under the Act, for which the employee is entitled to receive under the Act, excluding any benefit for medical, legal or rehabilitation expenses.</p><p>E. Supplemental long-term disability benefits shall be payable only during periods of total disability or partial disability.</p><p>F. Unless otherwise directed, to be eligible for benefits under this section, the employee must apply for Social Security disability benefits.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>.</p>',8),r=[n];function o(s,p,d,c,m,b){return t(),i("div",null,r)}const g=e(l,[["render",o]]);export{h as __pageData,g as default};
