import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 51.1-1169 Cessation of supplemental disability benefits; service retirement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1169.md","filePath":"vacode/51.1-1169.md"}'),r={name:"vacode/51.1-1169.md"},o=a('<h1 id="§-51-1-1169-cessation-of-supplemental-disability-benefits-service-retirement" tabindex="-1">§ 51.1-1169 Cessation of supplemental disability benefits; service retirement. <a class="header-anchor" href="#§-51-1-1169-cessation-of-supplemental-disability-benefits-service-retirement" aria-label="Permalink to &quot;§ 51.1-1169 Cessation of supplemental disability benefits; service retirement.&quot;">​</a></h1><p>A. Supplemental disability benefits shall cease to be paid to a participating employee upon the first to occur of the following:</p><ol><li><p>The end of the period of supplemental disability coverage as provided in subsection E of § <a href="http://law.lis.virginia.gov/vacode/51.1-1163/" target="_blank" rel="noreferrer">51.1-1163</a> or subsection E of § <a href="http://law.lis.virginia.gov/vacode/51.1-1165/" target="_blank" rel="noreferrer">51.1-1165</a>;</p></li><li><p>The date of death of the participating employee;</p></li><li><p>On the date the employee attains normal retirement age; or</p></li><li><p>The effective date of the participating full-time employee&#39;s service retirement under the hybrid retirement program described in § <a href="http://law.lis.virginia.gov/vacode/51.1-169/" target="_blank" rel="noreferrer">51.1-169</a>.</p></li></ol><p>Notwithstanding the foregoing, an employee who is approved for supplemental disability benefits (i) at age 60 through 64 shall be eligible for five years of supplemental disability benefits, (ii) at age 65 through 68 shall be eligible for supplemental disability benefits to age 70, and (iii) at age 69 or older shall be eligible for supplemental disability benefits for one year. The eligibility periods include supplemental short-term disability and supplemental long-term disability.</p><p>B. Upon the cessation of benefits payable under the Act, a participating full-time employee may take service retirement under any provision of this title for which the employee is otherwise eligible, including the hybrid retirement program described in § <a href="http://law.lis.virginia.gov/vacode/51.1-169/" target="_blank" rel="noreferrer">51.1-169</a>. Such employee shall be authorized to elect any option for the payment of his retirement allowance provided under subsection A of § <a href="http://law.lis.virginia.gov/vacode/51.1-165/" target="_blank" rel="noreferrer">51.1-165</a>.</p><p>C. The employee&#39;s average final compensation shall be equal to his creditable compensation on the date of the commencement of the disability increased by an amount recommended by the actuary of the Virginia Retirement System, and approved by the Board, from the date of the commencement of the disability to the date of retirement.</p><p>D. The creditable service of a participating full-time employee taking service retirement pursuant to this section shall include periods during which the employee received supplemental disability benefits, provided that such creditable service shall not include periods for which (i) the employee received supplemental short-term disability benefits, (ii) the employer did not report such creditable service to the retirement system, and (iii) the employee did not purchase such creditable service.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>.</p>',8),l=[o];function n(s,p,d,h,c,f){return i(),t("div",null,l)}const g=e(r,[["render",n]]);export{b as __pageData,g as default};
