import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 51.1-1174 Life and accident insurance coverage during disability absences.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1174.md","filePath":"vacode/51.1-1174.md"}'),n={name:"vacode/51.1-1174.md"},r=t('<h1 id="§-51-1-1174-life-and-accident-insurance-coverage-during-disability-absences" tabindex="-1">§ 51.1-1174 Life and accident insurance coverage during disability absences. <a class="header-anchor" href="#§-51-1-1174-life-and-accident-insurance-coverage-during-disability-absences" aria-label="Permalink to &quot;§ 51.1-1174 Life and accident insurance coverage during disability absences.&quot;">​</a></h1><p>A. Participating employees participating in a group life and accident insurance program established pursuant to Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/51.1-500/" target="_blank" rel="noreferrer">51.1-500</a> et seq.) shall continue to participate in such program during periods of absence covered by short-term and long-term disability benefits.</p><p>B. During periods of absence covered by short-term disability benefits, the amount of the life insurance benefit shall be based on the annual salary of the participating employee at the commencement of the disability and shall be adjusted to include salary increases awarded during the period covered by short-term disability benefits.</p><p>C. During periods of absence covered by long-term disability benefits, the amount of the life insurance benefit shall be based on the annual salary of the participating employee at the commencement of the disability. Such amount shall not include salary increases awarded during the period covered by long-term disability benefits.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>.</p>',5),s=[r];function o(c,d,l,p,b,h){return a(),i("div",null,s)}const g=e(n,[["render",o]]);export{u as __pageData,g as default};
