import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1178 Board authorized to provide long-term care insurance and benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1178.md","filePath":"vacode/51.1-1178.md"}'),n={name:"vacode/51.1-1178.md"},i=t('<h1 id="§-51-1-1178-board-authorized-to-provide-long-term-care-insurance-and-benefits" tabindex="-1">§ 51.1-1178 Board authorized to provide long-term care insurance and benefits. <a class="header-anchor" href="#§-51-1-1178-board-authorized-to-provide-long-term-care-insurance-and-benefits" aria-label="Permalink to &quot;§ 51.1-1178 Board authorized to provide long-term care insurance and benefits.&quot;">​</a></h1><p>A. For purposes of this section, &quot;participating employee&quot; means the same as that term is defined in § <a href="http://law.lis.virginia.gov/vacode/51.1-1150/" target="_blank" rel="noreferrer">51.1-1150</a>.</p><p>B. The Board is authorized to develop, implement, and administer a long-term care insurance program for participating employees. The Board may contract for and purchase such long-term care insurance or may self-insure long-term care benefits or may use such other actuarially sound funding necessary to effectuate such long-term care insurance and benefits.</p><p>C. Employers of participating employees shall pay to the Board contribution amounts, to be determined by the Board, to provide the Board with such funds as shall be required from time to time to (i) obtain and maintain long-term care insurance and benefits for participating employees and (ii) administer the long-term care insurance program, including providing case management and cost containment programs. Contributions shall be deposited in the Hybrid Retirement Program Disability Insurance Trust Fund established under § <a href="http://law.lis.virginia.gov/vacode/51.1-1183/" target="_blank" rel="noreferrer">51.1-1183</a>.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>.</p>',5),o=[i];function s(d,c,l,p,m,u){return a(),r("div",null,o)}const f=e(n,[["render",s]]);export{g as __pageData,f as default};
