import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 51.1-124.11 Recovery of payments procured by fraud, false statement, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-124.11.md","filePath":"vacode/51.1-124.11.md"}'),o={name:"vacode/51.1-124.11.md"},i=t('<h1 id="§-51-1-124-11-recovery-of-payments-procured-by-fraud-false-statement-etc" tabindex="-1">§ 51.1-124.11 Recovery of payments procured by fraud, false statement, etc. <a class="header-anchor" href="#§-51-1-124-11-recovery-of-payments-procured-by-fraud-false-statement-etc" aria-label="Permalink to &quot;§ 51.1-124.11 Recovery of payments procured by fraud, false statement, etc.&quot;">​</a></h1><p>Any payment to a member or beneficiary which is later determined by the Board to have been procured on the basis of any false statement or falsification of any Retirement System record knowingly made by or on behalf of the member or beneficiary, or the member&#39;s or beneficiary&#39;s failure to make any required report of change in disability status, may be recovered from the member or beneficiary by the Board either by way of a credit against future payments due the member or beneficiary, by an action at law against the member or beneficiary, or by deducting any overpayment of benefits from insurance proceeds as provided in § <a href="http://law.lis.virginia.gov/vacode/51.1-510/" target="_blank" rel="noreferrer">51.1-510</a>. Prior to making any such determination, the Board shall give the member or beneficiary reasonable prior written notice and an opportunity to be heard in accordance with the provisions of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.). Any member or beneficiary aggrieved by such determination of the Board shall be entitled to judicial review pursuant to Article 5 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4025/" target="_blank" rel="noreferrer">2.2-4025</a> et seq.) of the Administrative Process Act.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>.</p>',3),n=[i];function s(c,d,f,m,l,y){return r(),a("div",null,n)}const p=e(o,[["render",s]]);export{b as __pageData,p as default};
